describe('getSigningThresholdFromVerificationScript', () => {
  it('returns correct value(1)', () => {
    const result = getSigningThresholdFromVerificationScript('2102028a99826edc0c97d18e22b6932373d908d323aa7f92656a77ec26e8861699efac')
    result.should.equal(1)
  })

  it('returns correct value(2)', () => {
    const result = getSigningThresholdFromVerificationScript('522102028a99826edc0c97d18e22b6932373d908d323aa7f92656a77ec26e8861699ef21031d8e1630ce640966967bc6d95223d21f44304133003140c3b52004dc981349c92102232ce8d2e2063dce0451131851d47421bfc4fc1da4db116fca5302c0756462fa53ae')
    result.should.equal(2)
  })

  it('returns correct value(17)', () => {
    const result = getSigningThresholdFromVerificationScript('')
    result.should.equal(17)
  })
})

